<?php

namespace Terrific\Composition\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * ProjectRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProjectRepository extends EntityRepository
{
    public function getAll($user) {
        $em = $this->getEntityManager();

        $query = $em->createQuery('SELECT p FROM TerrificComposition:Project p WHERE p.user = :user')
            ->setParameter('user', $user);

        return $query->getResult();
    }

    public function create($user, $project) {
        $em = $this->getEntityManager();

        // create some default values
        $project->setUser($user);
        $project->setName('Lab');

        // persist it
        $em->persist($project);
        $em->flush();

        return $project;
    }

    public function update($user, $id, $tmpProject) {
        $project = $this->findOneByUserAndId($user, $id);

        // TODO: update logic

        return $project;
    }

    public function delete($user, $id) {
        $em = $this->getEntityManager();

        $project = $this->findOneByUserAndId($user, $id);

        $em->remove($project);
        $em->flush();
    }

    public function findOneByUserAndId($user, $id) {
        $em = $this->getEntityManager();

        $query = $em->createQuery('SELECT p FROM TerrificComposition:Project p WHERE p.id = :id AND p.user = :user')
            ->setParameter('id', $id)
            ->setParameter('user', $user)
            ->setMaxResults(1);

        $result = $query->getResult();
        $project = $result[0];

        if(!$project) {
            throw new \Exception('the project with the id "'.$id.'" could not be found');
        }

        return $project;
    }
}
